import { makeAutoObservable } from 'mobx';

export default class CartItemStore {
  constructor() {
    this._types = [
      /* { id: 1, name: 'Art ' },
      { id: 2, name: 'Sport ' },
      { id: 3, name: 'Music ' },
      { id: 4, name: 'Music ' },*/
    ];
    this._brands = [
      // тут нужно понимать что автори нфт
      { id: 1, name: 'Bob ' },
      { id: 2, name: 'Anna ' },
      { id: 3, name: 'Tanya ' },
    ];
    this._cartItem = [];
    this._selectedType = {};
    this._selectedBrand = {};
    makeAutoObservable(this); // следит за измениними и компонента будет делать перерендер

    // actions (как-то изменяють состояние)
  }
  setTypes(types) {
    this._types = types;
  }
  setBrands(brands) {
    this._brands = brands;
  }
  setCartItem(items) {
    this._cartItem = items;
  }
  setSelectType(type) {
    this._selectedType = type;
  }
  setSelectBrand(brand) {
    this._selectedBrand = brand;
  }
  get types() {
    return this._types;
  }
  get brands() {
    return this._brands;
  }
  get item() {
    return this._cartItem;
  }
  get selectType() {
    return this._selectedType;
  }
  get selectBrand() {
    return this._selectedBrand;
  }
}
