import React, { useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '..';
import CartItem from '../components/CartItem';
import Header from '../components/Header';
import { fetchDevices, fetchOneDevice } from '../htpp/itemAPI';
const ShopPage = observer(({ category }) => {
  const { cartItem } = useContext(Context);

  useEffect(() => {
    fetchDevices().then((data) => cartItem.setCartItem(data));
  }, []);
  return (
    <div className="carts">
      <div className="carts__container _container">
        {cartItem.brands.map((brand) => (
          <div
            className="carts__title"
            onClick={() => cartItem.setSelectBrand(brand)}
            key={brand.id}>
            {category}
          </div>
        ))}

        {cartItem.item.map((cartItem) => (
          <CartItem key={cartItem.id} cartItem={cartItem} />
        ))}
      </div>
    </div>
  );
});

export default ShopPage;
