import React, { useState } from 'react';
import { createBrand } from '../../htpp/itemAPI';

const CreateBrand = ({ active, setActive }) => {
  const [valueName, setValueName] = useState('');
  //const [valueImage, setValueImage] = useState('');
  console.log(active);

  const addType = () => {
    createBrand({ name: valueName }).then((data) => {
      console.log(data);
      setValueName('');

      setActive();
    });
    return (
      <div
        className={active ? 'createItem _active' : 'createItem'}
        onClick={() => setActive(false)}>
        <div className="createItem__container _container">
          <div className="createItem__inner" onClick={(e) => e.stopPropagation()}>
            <p className="createItem__title">New Category</p>
            <div className="inputblock">
              <div className="inputblock__title">Name of new Category</div>
              <input
                type="text"
                value={valueName}
                onChange={(e) => setValueName(e.target.value)}
                className="inputblock__input"
                placeholder="name"
              />

              <button className="createItem__button" onClick={addType}>
                Create
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };
};

export default CreateBrand;
