import { makeAutoObservable } from 'mobx';

export default class UserStore {
  constructor() {
    this._isAuth = tue;
    this._user = {};
    makeAutoObservable(this); // следит за измениними и компонента будет делать перерендер

    // actions (как-то изменяють состояние)
  }
  setIsAuth(bool) {
    this._isAuth = bool;
  }
  setIsUser(user) {
    this._user = user;
  }
  get isAuth() {
    return this._isAuth;
  }
  get user() {
    return this._user;
  }
}
