import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '..';
import CartItem from '../components/CartItem';
import Header from '../components/Header';

const ShopPage = observer(() => {
  const { cartItem } = useContext(Context);
  console.log(cartItem.item);
  return (
    <div className="carts">
      <div className="carts__container _container">
        {cartItem.brands.map((brand) => (
          <div
            className="carts__title"
            onClick={() => cartItem.setSelectBrand(brand)}
            key={brand.id}>
            {brand.name}
          </div>
        ))}

        {cartItem.item.map((cartItem) => (
          <div className="carts__menu menu">
            <div className="menu__item">
              vbb
              <img src={cartItem.img} alt="cart" />
              <div className="menu__information">
                <div className="menu__autor"></div>
                <div className="menu__name">{cartItem.name}</div>
                <div className="menu__price">{cartItem.price}</div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
});

export default ShopPage;
